import * as types from "../../actionTypes";
import AboutApi from "../../../api/AboutApi";

let actionsFunction = {

  //CRUD METHODS

  // Create about
  createAbout: function(about) {
    return function(dispatch) {
      return AboutApi
        .createAbout(about)
        .then(about => {
          dispatch(actionsFunction.createAboutSuccess(about));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createAboutSuccess: function(about) {
    return { type: types.CREATE_ABOUT_SUCCESS, payload: about };
  },


  // Delete about
  deleteAbout: function(id) {
    return function(dispatch) {
      return AboutApi
        .deleteAbout(id)
        .then(about => {
          dispatch(actionsFunction.deleteAboutSuccess(about));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteAboutSuccess: function(about) {
    return { type: types.DELETE_ABOUT_SUCCESS, payload: about };
  },


  // Get about
  loadAbout: function(id) {
    return function(dispatch) {
      return AboutApi
        .getOneAbout(id)
        .then(about => {
          dispatch(actionsFunction.loadAboutSuccess(about));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadAboutSuccess: function(about) {
    return { type: types.GET_ABOUT_SUCCESS, payload: about };
  },

  // Load  list
  loadAboutList: function() {
    return function(dispatch) {
      return AboutApi
        .getAboutList()
        .then(list => {
          dispatch(actionsFunction.loadAboutListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadAboutListSuccess: function(list) {
    return { type: types.LIST_ABOUT_SUCCESS, payload: list };
  },

	
  // Save about
  saveAbout: function(about) {
    return function(dispatch) {
      return AboutApi
        .saveAbout(about)
        .then(about => {
          dispatch(actionsFunction.saveAboutSuccess(about));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveAboutSuccess: function(about) {
    return { type: types.UPDATE_ABOUT_SUCCESS, payload: about };
  },


};

export default actionsFunction;
