import * as types from "../../actionTypes";
import StudentApi from "../../../api/StudentApi";

let actionsFunction = {

  //CRUD METHODS

  // Create student
  createStudent: function(student) {
    return function(dispatch) {
      return StudentApi
        .createStudent(student)
        .then(student => {
          dispatch(actionsFunction.createStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createStudentSuccess: function(student) {
    return { type: types.CREATE_STUDENT_SUCCESS, payload: student };
  },


  // Delete student
  deleteStudent: function(id) {
    return function(dispatch) {
      return StudentApi
        .deleteStudent(id)
        .then(student => {
          dispatch(actionsFunction.deleteStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteStudentSuccess: function(student) {
    return { type: types.DELETE_STUDENT_SUCCESS, payload: student };
  },


  // Find by _course
  findBy_course: function(key) {
    return function(dispatch) {
      return StudentApi
        .findBy_course(key)
        .then(item => {
          dispatch(actionsFunction.findBy_courseSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findBy_courseSuccess: function(item) {
    return { type: types.FINDBY_COURSE_STUDENT_SUCCESS, payload: item };
  },


  // Get student
  loadStudent: function(id) {
    return function(dispatch) {
      return StudentApi
        .getOneStudent(id)
        .then(student => {
          dispatch(actionsFunction.loadStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadStudentSuccess: function(student) {
    return { type: types.GET_STUDENT_SUCCESS, payload: student };
  },

  // Load  list
  loadStudentList: function() {
    return function(dispatch) {
      return StudentApi
        .getStudentList()
        .then(list => {
          dispatch(actionsFunction.loadStudentListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadStudentListSuccess: function(list) {
    return { type: types.LIST_STUDENT_SUCCESS, payload: list };
  },

	
  // Save student
  saveStudent: function(student) {
    return function(dispatch) {
      return StudentApi
        .saveStudent(student)
        .then(student => {
          dispatch(actionsFunction.saveStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveStudentSuccess: function(student) {
    return { type: types.UPDATE_STUDENT_SUCCESS, payload: student };
  },


  /*
  Name: validate
  Description: Nothing
  Params: 
    String id - id of the exam
  */
  validate: function(...params) {
    return function(dispatch) {
      return StudentApi
        .validate(params)
        .then( result => {
          dispatch(actionsFunction.validateSuccess(result));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  validateSuccess: function(result) {
    return { type: types.VALIDATE_STUDENT_SUCCESS, payload: result };
  },
		
};

export default actionsFunction;
